import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faClose} from "@fortawesome/free-solid-svg-icons";
function AddPlayerForm({createNewPlayer, toggleForm}) {
    function handleSubmit(event) {
        event.preventDefault();
        // Read the form data
        const form = event.target;
        const formData = new FormData(form);

        // console.log(formData);
        createNewPlayer(formData);
        toggleForm(false);
    }

    return (
            <form onSubmit={handleSubmit} id={"player-form"}>
                <label htmlFor="name-input">Name</label>
                <input id="name-input" name={"name"} />

                <label htmlFor="player-input">Player</label>
                <input id="player-input" name={"player"} />

                <label htmlFor="level-input">Level</label>
                <input id="level-input" name={"level"} type={"number"}/>

                <label htmlFor="class-input">Class</label>
                <select id="class-input" name={"class"} >
                    <option value=""></option>
                </select>

                <label htmlFor="race-input">Race</label>
                <input id="race-input" name={"race"} />
                <div>
                    <button type="submit">Add</button>
                    <button type="reset">Reset</button>
                </div>

                <button id={"close-form"} onClick={() => toggleForm(false)}><FontAwesomeIcon icon={faClose} /></button>

            </form>
    )
}

export default AddPlayerForm;
