    function handleClick() {
        console.log(`showModal: ${showModal}`)
        setShowModal(prevState => !prevState);
    }
    return (
        <div className="App">
            <div id={"players-container"}>
                < Player/>
                < Player/>
                < Player/>
                < Player/>
            </div>
            <button onClick={handleClick}>Add player</button>
            {showModal && <AddPlayerForm/>}
        </div>
    );
}

export default App;
