import {useState} from "react";
import ReactModal from "react-modal"

import './App.css';

import Player from "./Player";
import AddPlayerForm from "./AddPlayerForm";

function App() {
    const TEST_PLAYERS = [
        {name: "Aragon", player: "", level: 0, clazz: "Fighter", race: "Human"},
        {name: "Aragon", player: "", level: 0, clazz: "Fighter", race: "Human"},
        {name: "Aragon", player: "", level: 0, clazz: "Fighter", race: "Human"}
    ];
    const [showModal, setShowModal] = useState(false);
    const [players, setPlayers] = useState(TEST_PLAYERS);
    function createNewPlayer(formData) {
        console.log(formData);
        setPlayers(prevState => [...prevState, formData])
    }

    return (
        <div className="App">
            <div id={"players-container"}>
                {players.map((player, index) => <Player id={`player${index}`} playerDetails={player}/>)}
            </div>
            <button onClick={() => setShowModal(true)}>New player</button>
            <ReactModal
                isOpen={showModal}
                appElement={document.getElementById("root")}
            >
                <AddPlayerForm createNewPlayer={createNewPlayer} toggleForm={setShowModal}/>
            </ReactModal>
        </div>
    );
}

export default App;
