import {useEffect, useState} from "react";
import ReactModal from "react-modal"

import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faEdit, faPlus} from "@fortawesome/free-solid-svg-icons";

import './App.css';

import Player from "./Player";
import AddPlayerForm from "./AddPlayerForm";
import {THURSDAY_PLAYERS, Person} from "./TestPlayers"

function App() {

    const [showModal, setShowModal] = useState(false);

    const [players, setPlayers] = useState(THURSDAY_PLAYERS);

    const [showTextEdit, setShowTextEdit] = useState(false);
    const [partyName, setPartyName] = useState("Unknown");
    function handlePartyTextInput(event) {
        console.log(event.target.value);
        setPartyName(event.target.value);
    }

    function createNewPlayer(formObj) {
        const person = new Person();
        console.log(person.totalKills);
        // console.log(formData);
        setPlayers(prevState => [...prevState, formObj]);
        // console.log(players);
    }

    useEffect(() => {
        // console.log("useEffect players");
        // console.log(players[0].totalKills);
        // sort players by kills, determining rank
        players.sort((a, b) => a.totalKills - b.totalKills);
    }, [players]);
    return (
        <div className="App">
            <header>
                <h1>TTRPG Kill Tracker</h1>
                <button onClick={() => setShowModal(true)}>New player
                    <FontAwesomeIcon icon={faPlus} />
                </button>
            </header>
            <main>
                <header>
                    <h2>Party: {!showTextEdit && <span>{partyName}</span>}</h2>
                    {showTextEdit && <input
                        type={"text"}
                        onChange={handlePartyTextInput}
                        aria-label={"party-name-text-input"}
                    />}
                    <button onClick={() =>
                        setShowTextEdit(prevState => !prevState)} >
                        Edit
                        <FontAwesomeIcon icon={faEdit}/></button>
                </header>
                <div id={"players-container"}>
                    {players.map((player, index) => <Player key={`player${index}`} playerDetails={player} rank={index}/>)}
                </div>
                <ReactModal
                    className={"modal"}
                    isOpen={showModal}
                    appElement={document.getElementById("root")}
                >
                    <AddPlayerForm createNewPlayer={createNewPlayer} toggleForm={setShowModal}/>
                </ReactModal>
            </main>

        </div>
    );
}

export default App;
