import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faSkull} from "@fortawesome/free-solid-svg-icons";

function PlayerStats({killStats}) {
    let total_kills = 0;
    try {
        total_kills = killStats.reduce((sum, stat) => sum + stat.count, 0);
    } catch (err) {

    }


    return (
        <div className={"player-stats"}>
            <FontAwesomeIcon icon={faSkull} />
            {killStats.map((entry, index) => <div key={index} className={"kill-list"}><p>{entry.type}</p><p>{entry.count}</p></div>)}
            <div className={"kill-list kill-list-total"}><p> Total Kills </p><p><strong>{total_kills}</strong></p></div>
        </div>
    )
}

export default PlayerStats;
