import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faSkull} from "@fortawesome/free-solid-svg-icons";

function KillStats({killStats}) {
    const TOTAL_KILLS = killStats.reduce((sum, stat) => sum + stat.kills, 0);

    return (
        <div className={"killstats-container"}>
            <h1>Stats</h1>
            <div><span><FontAwesomeIcon icon={faSkull} /> Total Kills </span><span>{TOTAL_KILLS}</span></div>
            {killStats.map((entry) => <div className={"kill-list"}><p>{entry.type}</p><p>{entry.kills}</p></div>)}
        </div>
    )
}

export default KillStats;
