import {useState} from "react";
import ReactModal from "react-modal"

import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faEdit} from "@fortawesome/free-solid-svg-icons";

import './App.css';

import Player from "./Player";
import AddPlayerForm from "./AddPlayerForm";
import {TEST_PLAYERS} from "./TestPlayers"

function App() {

    const [showModal, setShowModal] = useState(false);

    const [players, setPlayers] = useState(TEST_PLAYERS);

    const [showTextEdit, setShowTextEdit] = useState(false);
    const [partyName, setPartyName] = useState("Unknown");
    function handlePartyTextInput(event) {
        console.log(event.target.value);
        setPartyName(event.target.value);
    }

    function createNewPlayer(formData) {
        // console.log(formData);
        setPlayers(prevState => [...prevState, Object.fromEntries(formData.entries())]);
        // console.log(players);
    }

    return (
        <div className="App">
            <header>
                <h2>Party: {!showTextEdit && <span>{partyName}</span>}</h2>
                {showTextEdit && <input
                    type={"text"}
                    onChange={handlePartyTextInput}
                    aria-label={"party-name-text-input"}
                />}
                <button onClick={() => setShowTextEdit(prevState => !prevState)} >Edit<FontAwesomeIcon icon={faEdit}/></button>
            </header>
            <div id={"players-container"}>
                {players.map((player, index) => <Player key={`player${index}`} playerDetails={player}/>)}
            </div>
            <button onClick={() => setShowModal(true)}>New player</button>
            <ReactModal
                className={"modal"}
                isOpen={showModal}
                appElement={document.getElementById("root")}
            >
                <AddPlayerForm createNewPlayer={createNewPlayer} toggleForm={setShowModal}/>
            </ReactModal>
        </div>
    );
}

export default App;
