import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faSkull} from "@fortawesome/free-solid-svg-icons";

function PlayerStats({killStats}) {
    const TOTAL_KILLS = killStats.reduce((sum, stat) => sum + stat.kills, 0);

    return (
        <div className={"player-stats"}>
            <FontAwesomeIcon icon={faSkull} />
            {killStats.map((entry, index) => <div key={index} className={"kill-list"}><p>{entry.type}</p><p>{entry.kills}</p></div>)}
            <div className={"kill-list total"}><p> Total Kills </p><p><strong>{TOTAL_KILLS}</strong></p></div>
        </div>
    )
}

export default PlayerStats;
